        "{requestId:5,ds:android,muid:1695063496243-482a4602-c03e-4858-80e0-fb94ba5045bc,d:[],cdsnum:1699965728034-san0000684-b2ac5225-511b-415b-8876-af49b575db65,sts:gAAAAABlU2s1TUKWPEqK7BWY93opEZjM6L-T1ng9pAD6qztj-ue_VDA6IXHYPjjZ7ZV44S8LuMq6imgBc-5b-C-ebOJzDIqwPeA1L5PzGJBqM58vdOj1MK9ax7XY4aeXnevlEYiATrd5muaQzCr4xy5_ZGdeHk6hBpJvsua5jALFH1be1Ys86cCqFMVTT1ZBRFjas4J_4oRszchEH4iVPa2a5pEJQ6UrVkZBmZQspR8JdNIKZAk3Qc2OjgM4appzfD-hAmcM4EwkLEWfJJ2rg1ThSBDP6gWni4_ON8fzb2MAYDsknEczuegu8Yf1o64IUgyEAq7TU4YWThQNgmVb_ko5-BPxDvZTDg==,std:gAAAAABlU2s1V-ypyJul4t_6EqiXMsgUFaKGjXqruOnF7yOqYxW1K6HOq6XigpvvYdLLFFFyMdBx94GCZ0ryyQkGC6nui4oZPuwGGOO6WG-KaOBx6xqxILcmTMyi8m_RF0YOZ47h8-VMTaWa_2E8gGYuZVct-OjoX06UQgKv9yvqZZMTG26ogDY=}"

class MainViewModel: ViewModel() {
    private val timer = Timer()

    fun onStartClicked(textValue: String) {
        timer.scheduleAtFixedRate(object : TimerTask(){
            override fun run() {
                val h = hashUtils(DATA_HASH)
                Log.d("hashUtils", "h: $h")
            }

        }, 0, textValue.toInt() * 1000L)
    }

    fun onStopClicked(){
        timer.cancel()
    }

    fun hashUtils(data: String): String {
        val md = MessageDigest.getInstance("SHA-256")
        val hashBytes = md.digest(data.toByteArray(Charsets.UTF_8))
        return hashBytes.joinToString("") { "%02x".format(it) }
    }
}